#include "BigInteger.hpp"
#include "simple_clock.hpp"

#include <chrono>
#include <cmath>
#include <cstdlib>
#include <iomanip>
#include <iostream>
#include <map>
#include <memory>
#include <sstream>
#include <string>
#include <vector>

bool BigInteger_test () {
  std::cout << "BigInteger Test report\n";

  bool result = true;
  simple_time_t report[16];
  simple_timer_t ct, cta;

  std::cout << "\n|   action   ||          time          ||     error     |\n|------------||------------------------||---------------|\n| Creation   ||";
  // do calculation
  try {
    ct.start ();
    BigInteger
        a = "17109938276655544333234567880088776588224401929\0",
        b = "-790920875098776655544333234567880088776588224401\0";
    std::cout << std::setw (24) << std::setfill (' ') << ct.end ();
    // compare
    cta.start ();
    ct.start ();
    result = (b == b) && !(b == a);
    std::cout << "||       -       |\n|  •  ==     ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    ct.start ();
    result = (b != a) && !(b != b);
    std::cout << "|\n|  •  !=     ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    ct.start ();
    result = (b <= a) && !(a <= b);
    std::cout << "|\n|  •  <=     ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    ct.start ();
    result = (a >= b) && !(b >= a);
    std::cout << "|\n|  •  >=     ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    ct.start ();
    result = (b < a) && !(a < b);
    std::cout << "|\n|  •  <      ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    ct.start ();
    result = (a > b) && !(b > a);
    std::cout << "|\n|  •  >      ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    std::cout << "|\n| Compare    ||" << std::setw (24) << std::setfill (' ') << cta.end ();

    // math
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    BigInteger A = a, B = b;
    cta.start ();
    ct.start ();
    result = ((b + b) == a);
    result &= ((a + (-b)) == b);
    result &= (((-a) + b) == (-b));
    result &= (((-b) + (-b)) == (-a));
    result &= ((B += b) == a);
    A = a, B = -b;
    result &= ((A += B) == b);
    A = -a;
    result &= ((A += b) == B);
    A = -a, B = -b;
    result &= ((B += B) == A);
    std::cout << "||       -       |\n|  •  Add    ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    ct.start ();
    result = ((a - b) == b);
    result &= (((-a) - (-b)) == (-b));
    result &= (((-b) - b) == (-a));
    result &= ((b - (-b)) == a);
    A = a;
    result &= ((A -= b) == b);
    A = -a, B = -b;
    result &= ((A -= B) == B);
    // B = -b;
    result &= ((B -= b) == -a);
    B = b;
    result &= ((B -= (-b)) == a);
    std::cout << "|\n|  •  Sub    ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    ct.start ();
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result = ((a * a) == b);
    result &= (((-a) * (-a)) == b);
    result &= ((a * (-a)) == (-b));
    result &= (((-a) * a) == (-b));
    result &= ((a *= a) == b);
    a = BigInteger ("-\0");
    b = BigInteger ("\0");
    result &= ((a *= a) == b);
    a = BigInteger ("\0");
    b = BigInteger ("-\0");
    result &= ((a *= (-a)) == b);
    a = BigInteger ("-\0");
    b = BigInteger ("-\0");
    result &= ((a *= (-a)) == b);
    std::cout << "|\n|  •  Mul    ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    ct.start ();
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result = ((b / a) == a);
    result &= ((b / (-a)) == (-a));
    result &= (((-b) / (-a)) == a);
    result &= (((-b) / a) == (-a));
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b /= a) == a);
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b /= (-a)) == (-a));
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= (((-b) /= (-a)) == a);
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= (((-b) /= a) == (-a));
    std::cout << "|\n|  •  Div    ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    a = BigInteger ("\0"), b = BigInteger ("\0");
    ct.start ();
    result = ((b % a) == 4);
    result &= ((b %= a) == 4);
    std::cout << "|\n|  •  Mod    ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    ct.start ();
    a = BigInteger ("1152921504606846976\0"), b = 2;
    result = (b.pow (60) == a);
    std::cout << "|\n|  •  ^      ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    ct.start ();
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result = (b.sqrt () == a);
    std::cout << "|\n|  •  √      ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");
    std::cout << "|\n| Math       ||" << std::setw (24) << std::setfill (' ') << cta.end ();

    cta.start ();

    ct.start ();
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((b << (size_t)60) == a);
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result &= ((b <<= (size_t)60) == a);
    std::cout << "||       -       |\n|  •  <<     ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    ct.start ();
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((a >> (size_t)60) == b);
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result &= ((a >>= (size_t)60) == b);
    std::cout << "|\n|  •  >>     ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    ct.start ();
    a = BigInteger ("8589934592"), b = BigInteger ("120");
    result = ((a & b) == BigInteger (0));
    A = a;
    result = ((A &= b) == BigInteger (0));
    std::cout << "|\n|  •  & (AND)||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    ct.start ();
    a = BigInteger ("8589934592"), b = BigInteger ("120");
    result = ((a | b) == (a + b));
    a = BigInteger ("8589934592"), b = BigInteger ("120");
    A = a;
    result = ((A |= b) == (a + b));
    std::cout << "|\n|  •  | (OR) ||" << std::setw (24) << std::setfill (' ') << ct.end ();
    std::cout << (result ? "||       -       " : "|| result wrong! ");

    std::cout << "|\n| Binary O   ||" << std::setw (24) << std::setfill (' ') << cta.end () << "||       -       |";

  } catch (const char *msg) {
    std::cout << "Error has occure " << msg << std::endl;
    return false;
  }
  std::cout << std::endl;
  return true;
}