#include "BigInteger.hpp"
#include "clock_adjustment.hpp"

#include <iomanip>
#include <iostream>
#include <memory>
#include <string>
#include <map>

bool BigInteger_test () {
  std::cout << "BigInteger code test" << std::endl;
  bool result = true;
  try {
  	std::map<std::string,unsigned long> obj;
    profiling::clock_adjustment _clock ("Big Integer Operator Test");
    BigInteger
        a = "17109938276655544333234567880088776588224401929\0",
        b = "-790920875098776655544333234567880088776588224401\0";
    obj["Make"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // compare
    result = (a > b) && !(b > a);
    if (!result) throw ("    '>' operator error");
    result = (b < a) && !(a < b);
    if (!result) throw ("    '<' operator error");
    result = (a >= b) && !(b >= a);
    if (!result) throw ("    '>=' operator error");
    result = (b <= a) && !(a <= b);
    if (!result) throw ("    '<=' operator error");
    result = (b == b) && !(b == a);
    if (!result) throw ("    '==' operator error");
    result = (b != a) && !(b != b);
    if (!result) throw ("    '!=' operator error");
    obj["Compare"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // arithmatics operation test
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b + b) == a);
    result &= ((a + (-b)) == b);
    result &= (((-a) + b) == (-b));
    result &= (((-b) + (-b)) == (-a));
    if (!result) throw ("    '+' operator error");
    obj["+"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    result &= ((a - b) == b);
    result &= (((-a) - (-b)) == (-b));
    result &= (((-b) - b) == (-a));
    result &= ((b - (-b)) == a);
    if (!result) throw ("    '-' operator error");
    obj["-"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    BigInteger A = a, B = b;
    result &= ((B += b) == a);
    if (!result) throw ("    '+=' operator error");
    A = a, B = -b;
    result &= ((A += B) == b);
    if (!result) throw ("    '+=' operator error");
    A = -a;
    result &= ((A += b) == B);
    if (!result) throw ("    '+=' operator error");
    A = -a, B = -b;
    result &= ((B += B) == A);
    if (!result) throw ("    '+=' operator error");
    obj["+="] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    A = a;
    result &= ((A -= b) == b);
    A = -a, B = -b;
    result &= ((A -= B) == B);
    // B = -b;
    result &= ((B -= b) == -a);
    B = b;
    result &= ((B -= (-b)) == a);
    if (!result) throw ("    '-=' operator error");
    obj["-="] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((a * a) == b);
    result &= (((-a) * (-a)) == b);
    result &= ((a * (-a)) == (-b));
    result &= (((-a) * a) == (-b));
    if (!result) throw ("    '*' operator error");
    obj["*"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    result &= ((b / a) == a);
    result &= ((b / (-a)) == (-a));
    result &= (((-b) / (-a)) == a);
    result &= (((-b) / a) == (-a));
    if (!result) throw ("    '/' operator error");
    obj["/"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // "*=" operator test
    result &= ((a *= a) == b);
    a = BigInteger ("-\0");
    b = BigInteger ("\0");
    result &= ((a *= a) == b);
    a = BigInteger ("\0");
    b = BigInteger ("-\0");
    result &= ((a *= (-a)) == b);
    a = BigInteger ("-\0");
    b = BigInteger ("-\0");
    result &= ((a *= (-a)) == b);
    if (!result) throw ("    '*=' operator error");
    obj["*="] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // "/=" operator test
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b /= a) == a);
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b /= (-a)) == (-a));
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= (((-b) /= (-a)) == a);
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= (((-b) /= a) == (-a));
    if (!result) throw ("    '/=' operator error");
    obj["/="] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    a = BigInteger ("\0"), b = BigInteger ("\0");
    result = ((b % a) == 4);
    if (!result) throw ("    '\%' operator error");
    obj["%"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // "%=" operator test
    result = ((b %= a) == 4);
    if (!result) throw ("    '%=' operator error");
    obj["%="] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // ">>" operator test
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((a >> 60) == b);
    if (!result) throw ("    '>>' operator error");
    // "<<" operator test
    result = ((b << 60) == a);
    if (!result) throw ("    '<<' operator error");
    // "&" operator test
    a = BigInteger ("8589934592"), b = BigInteger ("120");
    result = ((a & b) == BigInteger (0));
    if (!result) throw ("    '&' operator error");
    result = ((a | b) == (a + b));
    if (!result) throw ("    '|' operator error");
    // ">>=" operator test
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((a >>= 60) == b);
    if (!result) throw ("    '>>=' operator error");
    // "<<=" operator test
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((b <<= 60) == a);
    if (!result) throw ("    '<<=' operator error");
    // "&=" operator test
    a = BigInteger ("8589934592"), b = BigInteger ("120");
    A = a;
    result = ((A &= b) == BigInteger (0));
    if (!result) throw ("    '&=' operator error");
    // "|=" operator test
    A = a;
    result = ((A |= b) == (a + b));
    if (!result) throw ("    '|=' operator error");
    obj["Bitwise"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    a = BigInteger ("1152921504606846976\0"), b = 2;
    // "pow" operator test
    result = (b.pow (60) == a);
    if (!result) throw ("    '^=' operator error");
    obj["pow"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // âˆš operator test
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result = (b.sqrt () == a);
    if (!result) throw ("    sqrt function error");
    obj["sqrt"] = _clock.get_clock (profiling::clock_adjustment::period::microseconds);
    // operator end of operator_clock
    std::cout << "Result: " << std::endl;
    for (std::pair<const char*,unsigned long> n : obj) {
    	std::cout << n.first << ": ";
    	unsigned long T = n.second;
    	if (T > 1000000000) {
    		std::cout << T / 1000000000 << " s ";
    		T %= 1000000000;
    	}
    	if (T > 1000000) {
    		std::cout << T / 1000000 << " ms ";
    		T %= 1000000;
    	}
    	if (T > 1000) {
    		std::cout << T << " us";
    	}
    	std::cout << std::endl;
    }
 
  } catch (const char *msg) {
    std::cout << "Error has occure " << msg << std::endl;
  }
  std::cout << "BigInteger code test, End" << std::endl;

  return result;
}