#include "BigInteger.hpp"
#include "clock_adjustment.hpp"

#include <iomanip>
#include <iostream>
#include <memory>

bool BigInteger_test () {
  std::cout << "BigInteger code test" << std::endl;
  bool result = true;
  try {
    profiling::clock_adjustment _clock ("Big Integer Operator Test");
    BigInteger
        a = "17109938276655544333234567880088776588224401929\0",
        b = "-790920875098776655544333234567880088776588224401\0";
    // try print
    std::cout << "Samples : ";
    std::cout << a << " & " << b << std::endl;

    {
      char *As = a.to_chars ();
      char *Bs = b.to_chars ();
      std::cout << "Samples : " << As << " & " << Bs << std::endl;
      delete[] As;
      delete[] Bs;
    }

    std::cout << "Object Generate : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // compare
    result = (a > b) && !(b > a);
    if (!result) throw ("    '>' operator error");
    result = (b < a) && !(a < b);
    if (!result) throw ("    '<' operator error");
    result = (a >= b) && !(b >= a);
    if (!result) throw ("    '>=' operator error");
    result = (b <= a) && !(a <= b);
    if (!result) throw ("    '<=' operator error");
    result = (b == b) && !(b == a);
    if (!result) throw ("    '==' operator error");
    result = (b != a) && !(b != b);
    if (!result) throw ("    '!=' operator error");
    std::cout << "Compare Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // arithmatics operation test
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b + b) == a);
    result &= ((a + (-b)) == b);
    result &= (((-a) + b) == (-b));
    result &= (((-b) + (-b)) == (-a));
    if (!result) throw ("    '+' operator error");
    std::cout << "+ Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    result &= ((a - b) == b);
    result &= (((-a) - (-b)) == (-b));
    result &= (((-b) - b) == (-a));
    result &= ((b - (-b)) == a);
    if (!result) throw ("    '-' operator error");
    std::cout << "- Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    BigInteger A = a, B = b;
    result &= ((B += b) == a);
    if (!result) throw ("    '+=' operator error");
    A = a, B = -b;
    result &= ((A += B) == b);
    if (!result) throw ("    '+=' operator error");
    A = -a;
    result &= ((A += b) == B);
    if (!result) throw ("    '+=' operator error");
    A = -a, B = -b;
    result &= ((B += B) == A);
    if (!result) throw ("    '+=' operator error");
    std::cout << "+= Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    A = a;
    result &= ((A -= b) == b);
    A = -a, B = -b;
    result &= ((A -= B) == B);
    // B = -b;
    result &= ((B -= b) == -a);
    B = b;
    result &= ((B -= (-b)) == a);
    if (!result) throw ("    '-=' operator error");
    std::cout << "-= Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((a * a) == b);
    result &= (((-a) * (-a)) == b);
    result &= ((a * (-a)) == (-b));
    result &= (((-a) * a) == (-b));
    if (!result) throw ("    '*' operator error");
    std::cout << "* Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    result &= ((b / a) == a);
    result &= ((b / (-a)) == (-a));
    result &= (((-b) / (-a)) == a);
    result &= (((-b) / a) == (-a));
    if (!result) throw ("    '/' operator error");
    std::cout << "/ Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // "*=" operator test
    result &= ((a *= a) == b);
    a = BigInteger ("-\0");
    b = BigInteger ("\0");
    result &= ((a *= a) == b);
    a = BigInteger ("\0");
    b = BigInteger ("-\0");
    result &= ((a *= (-a)) == b);
    a = BigInteger ("-\0");
    b = BigInteger ("-\0");
    result &= ((a *= (-a)) == b);
    if (!result) throw ("    '*=' operator error");
    std::cout << "*= Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // "/=" operator test
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b /= a) == a);
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= ((b /= (-a)) == (-a));
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= (((-b) /= (-a)) == a);
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result &= (((-b) /= a) == (-a));
    if (!result) throw ("    '/=' operator error");
    std::cout << "/= Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    a = BigInteger ("\0"), b = BigInteger ("\0");
    result = ((b % a) == 4);
    if (!result) throw ("    '\%' operator error");
    std::cout << "\% Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // "%=" operator test
    result = ((b %= a) == 4);
    if (!result) throw ("    '%=' operator error");
    std::cout << "\%= Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // ">>" operator test
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((a >> 60) == b);
    if (!result) throw ("    '>>' operator error");
    // "<<" operator test
    result = ((b << 60) == a);
    if (!result) throw ("    '<<' operator error");
    // "&" operator test
    a = BigInteger ("8589934592"), b = BigInteger ("120");
    result = ((a & b) == BigInteger (0));
    if (!result) throw ("    '&' operator error");
    result = ((a | b) == (a + b));
    if (!result) throw ("    '|' operator error");
    // ">>=" operator test
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((a >>= 60) == b);
    if (!result) throw ("    '>>=' operator error");
    // "<<=" operator test
    a = BigInteger ("1152921504606846976000000000000000000\0"), b = BigInteger ("1000000000000000000\0");
    result = ((b <<= 60) == a);
    if (!result) throw ("    '<<=' operator error");
    // "&=" operator test
    a = BigInteger ("8589934592"), b = BigInteger ("120");
    A = a;
    result = ((A &= b) == BigInteger (0));
    if (!result) throw ("    '&=' operator error");
    // "|=" operator test
    A = a;
    result = ((A |= b) == (a + b));
    if (!result) throw ("    '|=' operator error");
    std::cout << "All Bitwise Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    a = BigInteger ("1152921504606846976\0"), b = 2;
    // "pow" operator test
    result = (b.pow (60) == a);
    if (!result) throw ("    '^=' operator error");
    std::cout << "All Power Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // √ operator test
    a = BigInteger ("\0");
    b = BigInteger ("\0");
    result = (b.sqrt () == a);
    if (!result) throw ("    sqrt function error");
    std::cout << "√ Operator : " << _clock.get_clock (profiling::clock_adjustment::period::microseconds) << " us " << std::endl;
    // operator end of operator_clock
  } catch (const char *msg) {
    std::cout << "Error has occure " << msg << std::endl;
  }
  std::cout << "BigInteger code test, End" << std::endl;

  return result;
}