    result &= ((a * (-a)) == (-b));
    result &= (((-a) * a) == (-b));
    if (!result) throw ("    '*' operator error");
    std::cout << "* Operator : " << _clock.get_clock(clock_adjustment::period::microseconds) << " us " << std::endl;
    result &= ((b / a) == a);
    result &= ((b / (-a)) == (-a));
    result &= (((-b) / (-a)) == a);
    result &= (((-b) / a) == (-a));
    if (!result) throw ("    '/' operator error");
